import React, { Component } from 'react';
import MontantAvecTaxes from '../util/MontantAvecTaxes';

var montantAvecTaxe = new MontantAvecTaxes(2023, 0, 0, 0, 0)

class TaxeQC extends Component {

    state = {
        montantAvecTaxe
    }

    changerAnnee(year){
        let newValue = this.state.montantAvecTaxe.annee
        this.setState(() => ({
            newValue: year
        }));
    }

    changerMontantSansTaxe(montant){
        let newValue = this.state.montantAvecTaxe.montantSansTaxe
        this.setState(() => ({
            newValue: montant
            
        }));
    }

    changerTps(montant){
        let newValue = this.state.montantAvecTaxe.tps
        this.setState(() => ({
            newValue: montant
        }));
    }

    changerTvq(montant){
        let newValue = this.state.montantAvecTaxe.tvq
        this.setState(() => ({
            newValue: montant
        }));
    }

    changerMontantAvecTaxes(montant){
        let newValue = this.state.montantAvecTaxe.annee
        this.setState(() => ({
            newValue: montant
        }));
    }

    render() {
        return (
            <div className='App'>
                <fieldset>
                    <legend>Choisissez l'ann√©e du calcul:</legend>

                    <select value={this.state.montantAvecTaxe.annee} onChange={(e) => this.changerAnnee(e.target.value)}>
                        <option>2023</option>
                        <option>2022</option>
                        <option>2021</option>
                        <option>2020</option>
                        <option>2019</option>
                        <option>2018</option>
                        <option>2017</option>
                        <option>2016</option>
                        <option>2015</option>
                        <option>2014</option>
                        <option>2013</option>
                        <option>2012</option>
                        <option>2011</option>
                    </select>

                    <form>
                        <p>
                            <legend>Entrez le montant avant taxe :</legend>
                            <input value="50" onChange={(e) => this.changerMontantSansTaxe(e.target.value)}></input>
                        </p>

                        <legend>TPS :</legend>
                        <input type="text" name="tps" readonly value="2.5 $"></input>

                        <legend>TVQ :</legend>
                        <input type="text" name="tvq" readonly value="4.99 $"></input>

                        <legend>Montant Total :</legend>
                        <input type="text" name="montantAvecTaxe" readonly="" value="57.49 $"></input>
                    </form>
                </fieldset>
            </div>
        );
    }
}

export default TaxeQC;
