const TPS = 0.05
const TVQ_2013 = 0.09975
const TVQ_2012 = 0.095
const TVQ_2011 = 0.085

function calculTPS(montantSansTaxe){
    return montantSansTaxe * TPS
}

function calculTVQ(montantSansTaxe, annee){
    if(annee >= 2013){
        return montantSansTaxe * TVQ_2013
    }
    
    else if (annee == 2012) {
        return montantSansTaxe * TVQ_2012
    }
    else {
        return montantSansTaxe * TVQ_2011
    }
}

export {
    calculTPS, calculTVQ
}
