import React, { Component } from 'react';
import MontantAvecTaxes from '../util/MontantAvecTaxes';


class TaxeQC extends Component {

    state = {
        annee: 2023,
        montantSansTaxe: 0,
        tps: 0, 
        tvq: 0, 
        montantAvecTaxes: 0
    }

    //onChange={(e) => this.changerNombre2(e.target.value)}

    changerAnnee(year){
        this.setState(() => ({
            annee: year
        }));
    }

    changerMontantSansTaxe(montant){
        this.setState(() => ({
            montantSansTaxe: montant
        }));
    }

    changerTps(montant){
        this.setState(() => ({
            tps: montant
        }));
    }

    changerTvq(montant){
        this.setState(() => ({
            tvq: montant
        }));
    }

    changerMontantAvecTaxes(montant){
        this.setState(() => ({
            montantAvecTaxes: montant
        }));
    }

    render() {
        return (
            <div className='App'>
                <fieldset>
                    <legend>Choisissez l'ann√©e du calcul:</legend>

                    <select value={this.annee} onChange={(e) => this.changerAnnee(e.target.value)}>
                        <option>2023</option>
                        <option>2022</option>
                        <option>2021</option>
                        <option>2020</option>
                        <option>2019</option>
                        <option>2018</option>
                        <option>2017</option>
                        <option>2016</option>
                        <option>2015</option>
                        <option>2014</option>
                        <option>2013</option>
                        <option>2012</option>
                        <option>2011</option>
                    </select>

                    <form>
                        <p>
                            <legend>Entrez le montant avant taxe :</legend>
                            <input value="50" onChange={(e) => this.changerMontantSansTaxe(e.target.value)}></input>
                        </p>

                        <legend>TPS :</legend>
                        <input type="text" name="tps" readonly="" value="2.5 $"></input>

                        <legend>TVQ :</legend>
                        <input type="text" name="tvq" readonly="" value="4.99 $"></input>

                        <legend>Montant Total :</legend>
                        <input type="text" name="montantAvecTaxe" readonly="" value="57.49 $"></input>
                    </form>
                </fieldset>
            </div>
        );
    }
}

export default TaxeQC;
