import React, { Component } from 'react';
import { MontantAvecTaxes } from '../util/MontantAvecTaxes';

class TaxeQC extends Component {

    constructor(props) {
        super(props);
        this.changerMontantAvecTaxe = this.changerMontantAvecTaxe.bind(this);
    }

    state = {
        montantAvecTaxe: new MontantAvecTaxes(2023, 50)
    }

    changerAnnee = (year) => {
        const newMontant = new MontantAvecTaxes(year, this.state.montantAvecTaxe.getMontantSansTaxe())

        this.setState(() => ({
            montantAvecTaxe: newMontant
        }));
    }

    changerMontantAvecTaxe = (montant) =>{
        const newMontant = new MontantAvecTaxes(this.state.montantAvecTaxe.getYear() , montant)

        this.setState(() => ({
            montantAvecTaxe: newMontant
        }));
    }

    arrondirMontant(montant) {
        return Math.round(montant * 100) / 100 + " $"
    }

    render() {
        const ans = ["2023", "2022", "2021", "2020", "2019", "2018", "2017", "2016", "2015", "2014", "2013", "2012", "2011"]
        return (
            <div className='App'>
                <fieldset>
                    <legend>Choisissez l'ann√©e du calcul:</legend>

                    <select onChange={(e) => this.changerAnnee(e.target.value)}>
                        {
                            ans.map((an, index) => {
                                return <option key={index}>{an}</option>
                            })
                        }
                    </select>

                    <form>
                        <p>
                            <legend>Entrez le montant avant taxe :</legend>
                            <input value={this.state.montantAvecTaxe.getMontantSansTaxe()} onChange={(e) => this.changerMontantAvecTaxe(e.target.value)}></input>
                        </p>

                        <legend>TPS :</legend>
                        <input type="text" name="tps" readonly value={this.arrondirMontant(this.state.montantAvecTaxe.getTPS())}></input>

                        <legend>TVQ :</legend>
                        <input type="text" name="tvq" readonly value={this.arrondirMontant(this.state.montantAvecTaxe.getTVQ())}></input>

                        <legend>Montant Total :</legend>
                        <input type="text" name="montantAvecTaxe" readonly value={this.arrondirMontant(this.state.montantAvecTaxe.getMontantAvecTaxe())}></input>
                    </form>
                </fieldset>
            </div>
        );
    }
}

export default TaxeQC;
