import {calculTPS, calculTVQ} from "./Mathematique";

export class MontantAvecTaxes {

    constructor(annee, montantSansTaxe) {
        this.annee = annee;
        this.montantSansTaxe = montantSansTaxe;
        this.tps = calculTPS(montantSansTaxe);
        this.tvq = calculTVQ(annee, montantSansTaxe);

        const calcul = this.montantAvecTaxe = parseFloat(this.montantSansTaxe) + parseFloat(this.tps) + parseFloat(this.tvq);
        calcul ? calcul : this.montantAvecTaxe = 0
    }

    getMontantSansTaxe() {
        return this.montantSansTaxe
    }

    getTPS(){
        return this.tps;
    }

    getTVQ(){
        return this.tvq;
    }

    getMontantAvecTaxe(){
        return this.montantAvecTaxe;
    }

    getYear(){
        return this.annee
    }
}
